<span class="line"><span style="color:#A6ACCD;">    </span><span style="color:#F07178;">stu</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;">stuReducer</span><span style="color:#89DDFF;">,</span></span>
