<span class="line"><span style="color:#F07178;">        openNotice</span><span style="color:#89DDFF;">:</span><span style="color:#A6ACCD;">nextProps</span><span style="color:#89DDFF;">.</span><span style="color:#A6ACCD;">openNotice</span></span>
